["custom"] = "function(states, event, unit, _, spellID)\n    if not unit then return end\n    \n    if event == \"NAME_PLATE_UNIT_REMOVED\" then\n        states[unit] = \n        {\n            show = false,\n            changed = true\n        }\n        return true\n    end\n    \n    if event ~= \"UNIT_SPELLCAST_SUCCEEDED\" then return end\n    if not spellID then return end\n    \n    local isDebug = false\n    if aura_env.config.debugMode.isDebugMode then\n        -- Ignore target GCDs:\n        if UnitIsUnit(unit, \"target\") then return end\n        isDebug = unit == \"player\"\n    else\n        if not UnitIsPlayer(unit) then return end\n        if UnitIsFriend(\"player\", unit) then return end\n    end\n    \n    -- Check if spell blacklisted:\n    if aura_env.IsSpellBlacklisted(spellID) then return end\n    \n    -- Get spec and role:\n    local specID, role = aura_env.GetUnitSpecAndRole(unit)\n    \n    -- Check if we should proceed based on settings and conditions:\n    local isProceeding = aura_env.config.displayMode == 1 -- Display Mode: All Enemies\n    \n    -- Check roles:\n    if not isProceeding and aura_env.config.displayMode == 2 and (role == \"HEALER\" or role == \"TANK\") then isProceeding = true end\n    if not isProceeding and aura_env.config.displayMode == 3 and role == \"HEALER\" then isProceeding = true end\n    \n    -- Check Anti Stun Mode:\n    if not isProceeding and aura_env.config.isAntiStunMode then\n        local activeTriggers = WeakAuras.GetActiveTriggers(aura_env.id)\n        if activeTriggers and activeTriggers[isDebug and 3 or 2] then -- In debug mode I'm testing this with additional aura trigger (#3) which simulates stun (it can be any buff/debuff).\n            if aura_env.CheckAntiStunMode(isDebug and \"target\" or unit, aura_env.config.antiStunModeMaxRange, specID) then -- In debug mode we check Anti Stun Mode conditions towards our target.\n                isProceeding = true\n            end\n        end\n    end\n    \n    -- Check CC Mode:\n    if not isProceeding and aura_env.config.isCCMode and aura_env.CheckCCMode(isDebug and \"target\" or unit, role) then -- In debug mode we check CC Mode conditions towards our target (except role).\n        isProceeding = true\n    end\n    \n    -- Check Bear Form:\n    -- if not isProceeding and not WA_GetUnitAura(unit, 5487) then\n    --     isProceeding = true\n    -- end\n    \n    if not isProceeding then return end\n    \n    -- Ignore spells with cast time (except channeled spells, they still trigger full GCD):\n    local spellName, _, icon, castTime = GetSpellInfo(spellID)\n    if not castTime or castTime > 0 then return end\n    \n    local isChanneling = UnitChannelInfo(unit)\n    local GCD = aura_env.CalculateGCD(unit, spellID)\n    \n    -- Debug print:\n    if isDebug and aura_env.config.debugMode.isShowDebugText then\n        local _, realGCD = GetSpellCooldown(61304)\n        print(string.format(\"%s | Calculated GCD: %.3f | Current GCD: %.3f | %s\", spellName, GCD, realGCD, isChanneling and \"Channel\" or \"Instant\"))\n    end\n    \n    -- Final checks:\n    if GCD == 0 then return end\n    if isChanneling and not aura_env.config.isShowChanneledSpellGCD then return end\n    \n    states[unit] =\n    {\n        show = true,\n        changed = true,\n        progressType = \"timed\",\n        value = 0,\n        total = 0,\n        duration = GCD,\n        expirationTime = GetTime() + GCD,\n        autoHide = true,\n        icon = icon,\n        unit = isDebug and aura_env.GetNameplateUnit(\"target\") or unit\n    }\n    \n    return true\nend",
