["init"] = {
["custom"] = "--[[\n    Notes:\n    All totems have fixed GCD of 1.0s unaffected by haste.\n    Enhancement Shamans have faster GCDs on Flame Shock and Frost Shock based on haste. With 0 haste it's the same as base GCD: 1.5s\n    Flat modifiers such as Aspect of the Wild are calculated first before any haste modifiers.\n    Haste modifier can bring GCD lowest to 0.75s. If flat modifiers made GCD 0.5s then it won't be modified by haste.\n    WW monks have some spells with GCD affected by haste and some not. Only way to handle this properly is to make a list of all spells which either ignore haste or not.\n]]\n\n-- https://wow.tools/dbc/?dbc=spelleffect&build=9.2.0.41726#page=1&colFilter[1]=107&colFilter[25]=exact%3A21\nlocal flatModByAura = {\n    [193530] = -0.2,    -- Aspect of the Wild\n    [13750] = -0.2,     -- Adrenaline Rush\n    [246558] = -0.2,    -- Lesser Adrenaline Rush\n}\n\n-- https://wow.tools/dbc/?dbc=spelleffect&build=9.2.0.41726#page=1&colFilter[1]=107&colFilter[25]=exact%3A21\nlocal flatModBySpec = { -- By default this will affect spells with 1.5s GCD. Spells with lower GCD must be listed additionally, like Expel Harm.\n    -- Brewmaster Monk:\n    [268] = {\n        mod = -0.5,\n        [322101] = true,    -- Expel Harm\n    },\n    -- Windwalker Monk:\n    [269] = {\n        mod = -0.5,\n        [322101] = true,    -- Expel Harm\n    },\n}\n\nlocal modByAuraAndSpell = {\n    [768] = {               -- Cat Form\n        [1850] = 0.0,       -- Dash\n        [219092] = 0.0,     -- Dash\n        [252216] = 0.0,     -- Tiger Dash\n        [137452] = 0.0,     -- Displacer Beast\n    }\n}\n\nlocal hasteModBySpec = {\n    [268] = 0.0,    -- Brewmaster Monk\n    [269] = 0.0,    -- Windwalker Monk\n    [259] = 0.0,    -- Assa\n    [260] = 0.0,    -- Outlaw\n    [261] = 0.0,    -- Sub\n}\n\n-- https://wow.tools/dbc/?dbc=spelleffect&build=9.2.0.41726&hotfixes=true#page=1&colFilter[1]=417\nlocal hasteModBySpell = {\n    -- Druid:\n    [22568] = 0.0,                  -- Ferocious Bite\n    [5221] = 0.0,                   -- Shred\n    [22570] = 0.0,                  -- Maim\n    [1822] = 0.0,                   -- Rake\n    [1079] = 0.0,                   -- Rip\n    [106785] = 0.0,                 -- Swipe\n    [106830] = 0.0,                 -- Thrash\n    [1850] = 0.0,                   -- Dash\n    [219092] = 0.0,                 -- Dash\n    [252216] = 0.0,                 -- Tiger Dash\n    \n    -- Rogue:\n    [185311] = 1.0,                 -- Crimson Vial\n    \n    -- Shaman:\n    [188389] = { [263] = 2.2, },    -- Flame Shock for Enhancement\n    [196840] = { [263] = 2.2, },    -- Frost Shock for Enhancement\n    -- Totems (All totems have fixed GCD of 1.0s unaffected by haste):\n    [204330] = 0.0,                 -- Skyfury Totem\n    [157153] = 0.0,                 -- Cloudburst Totem\n    [204336] = 0.0,                 -- Grounding Totem\n    [254416] = 0.0,                 -- Grounding Totem\n    [5394] = 0.0,                   -- Healing Stream Totem\n    [108280] = 0.0,                 -- Healing Tide Totem\n    [198838] = 0.0,                 -- Earthen Wall Totem\n    [98008] = 0.0,                  -- Spirit Link Totem\n    [8143] = 0.0,                   -- Tremor Totem\n    [208932] = 0.0,                 -- Tidal Totem\n    [207399] = 0.0,                 -- Ancestral Protection Totem\n    [2484] = 0.0,                   -- Earthbind Totem\n    [51485] = 0.0,                  -- Earthgrab Totem\n    [3599] = 0.0,                   -- Searing Totem\n    [324386] = 0.0,                 -- Vesper Totem\n    [324522] = 0.0,                 -- Vesper Totem\n    [204331] = 0.0,                 -- Counterstrike Totem\n    [16191] = 0.0,                  -- Mana Tide Totem\n    [192077] = 0.0,                 -- Wind Rush Totem\n    [192058] = 0.0,                 -- Capacitor Totem\n    [8512] = 0.0,                   -- Windfury Totem\n    [204332] = 0.0,                 -- Windfury Totem\n}\n\nlocal function GetArenaID(unit)\n    for i=1, 10 do\n        local arenaUnit = \"arena\" .. i\n        if UnitExists(arenaUnit) and UnitIsUnit(arenaUnit, unit) then\n            return i\n        end\n    end\nend\n\nlocal function GetUnitSpecAndRole(unit)\n    if unit == \"player\" then\n        local id, _, _, _, role = GetSpecializationInfo(GetSpecialization())\n        return id, role\n    end\n    \n    if not UnitInBattleground(\"player\") and not IsActiveBattlefieldArena() then return end\n    \n    local arenaID = GetArenaID(unit)\n    local specID = arenaID and GetArenaOpponentSpec(arenaID)\n    if specID and specID > 0 then\n        local role = select(5, GetSpecializationInfoByID(specID))\n        return specID, role\n    end\nend\naura_env.GetUnitSpecAndRole = GetUnitSpecAndRole\n\nlocal function CalculateGCD(unit, spellID, specID)\n    local _, baseGCD = GetSpellBaseCooldown(spellID)\n    if not baseGCD or baseGCD <= 0 then return 0 end\n    \n    local result = baseGCD/1000\n    \n    -- Flat spec modifiers\n    -- We do this only for spells with base GCD of 1.5s. This way it fits most spells with few exceptions.\n    specID = specID or GetUnitSpecAndRole(unit)\n    if specID and specID > 0 then\n        if flatModBySpec[specID] and (result == 1.5 or flatModBySpec[specID][spellID]) then\n            result = result + flatModBySpec[specID].mod\n        end\n    end\n    \n    -- Flat aura modifiers\n    for auraID, mod in pairs(flatModByAura) do\n        if WA_GetUnitAura(unit, auraID) then\n            result = result + mod\n            break\n        end\n    end\n    \n    -- Aura and spell modifiers:\n    for auraID, spellMods in pairs(modByAuraAndSpell) do\n        if spellMods[spellID] and WA_GetUnitAura(unit, auraID) then\n            result = result * spellMods[spellID]\n            break\n        end\n    end\n    \n    -- If GCD has already been reduced to 0.5 or below then just use this value, this is our absolute minimum\n    if result <= 0.5 then return result end\n    \n    -- Haste modifier\n    local hasteMod = 1.0\n    -- First by spec:\n    if specID and hasteModBySpec[specID] then\n        hasteMod = hasteModBySpec[specID]\n    end\n    -- Now override by spell (if present):\n    if hasteModBySpell[spellID] then\n        if type(hasteModBySpell[spellID]) == \"table\" then\n            if specID and hasteModBySpell[spellID][specID] then\n                hasteMod = hasteModBySpell[spellID][specID]\n            end\n        else\n            hasteMod = hasteModBySpell[spellID]\n        end\n    end\n    \n    result = math.max(0.75, result/(1.0 + (UnitSpellHaste(unit)*hasteMod)/100))\n    \n    return result\nend\naura_env.CalculateGCD = CalculateGCD\n\nlocal function GetNameplateUnit(unit)\n    for i=1, 40 do\n        local nameplateUnit = \"nameplate\" .. i\n        if UnitExists(nameplateUnit) and UnitIsUnit(unit, nameplateUnit) then\n            return nameplateUnit\n        end\n    end\nend\naura_env.GetNameplateUnit = GetNameplateUnit\n\nlocal function IsInRange(unit, maxRange)\n    return maxRange and (maxRange == 0 or WeakAuras.CheckRange(unit, maxRange, \"<=\"))\nend\n\nlocal function CheckAntiStunMode(unit, maxRange, specID)\n    local _, _, classIndex = UnitClass(unit)\n    if classIndex ~= 4 and classIndex ~= 11 then return false end -- Must be Rogue or Druid\n    if not IsInRange(unit, maxRange) then return false end\n    \n    -- Additional spec check for Druid:\n    if classIndex == 11 then\n        specID = specID or GetUnitSpecAndRole(unit)\n        if specID and specID == 103 then\n            return true\n        end\n    end\n    \n    return true\nend\naura_env.CheckAntiStunMode = CheckAntiStunMode\n\nlocal function GetRemainingCooldown(spellID)\n    local start, duration = GetSpellCooldown(spellID)\n    return start and (start + duration) - GetTime() or 0\nend\n\nlocal ccModeSpellList = { -- Default spell list for CC Mode\n    [187650] = 1,   -- Freezing Trap\n    [2094]   = 2,   -- Blind\n    [31661]  = 3,   -- Dragon's Breath\n    [8122]   = 4,   -- Psychic Scream\n}\nlocal function CheckCCMode(unit, role)\n    role = role or select(2, GetUnitSpecAndRole(unit))\n    if not role or role ~= \"HEALER\" then return false end\n    \n    -- Check default spells:\n    for spellID, optionID in pairs(ccModeSpellList) do\n        if IsPlayerSpell(spellID) and IsUsableSpell(spellID) and GetRemainingCooldown(spellID) <= 2 and IsInRange(unit, aura_env.config[\"ccModeSpell\" .. optionID .. \"Enabled\"] and aura_env.config[\"ccModeSpell\" .. optionID .. \"MaxRange\"]) then\n            return true\n        end\n    end\n    \n    -- Check custom spells:\n    for _, spellData in pairs(aura_env.config.ccModeCustomSpells) do\n        if IsPlayerSpell(spellData.spellID) and IsUsableSpell(spellData.spellID) and GetRemainingCooldown(spellData.spellID) <= 2 and IsInRange(unit, spellData.maxRange) then\n            return true\n        end\n    end\n    \n    return false\nend\naura_env.CheckCCMode = CheckCCMode\n\nlocal blacklistSpellList = { -- Default spell list for blacklist\n    [740]    = 1,   -- Tranquility\n    [324631] = 2,   -- Fleshcraft\n    [32379]  = 3,   -- Shadow Word: Death\n    [213610] = 4,   -- Holy Ward\n    [8143]   = 5,   -- Tremor Totem\n    [204336] = 6,   -- Grounding Totem\n    --[115310] = 7,   -- Revival\n    --[5487]   = 8,   -- Bear Form\n}\nlocal function IsSpellBlacklisted(spellID)\n    -- Check default spells:\n    if blacklistSpellList[spellID] then\n        return aura_env.config[\"blacklistSpell\" .. blacklistSpellList[spellID]]\n    end\n    \n    for _, spellData in pairs(aura_env.config.blacklistCustomSpells) do\n        if spellData.spellID == spellID then\n            return true\n        end\n    end\nend\naura_env.IsSpellBlacklisted = IsSpellBlacklisted\n\n--/dump GetSpellCooldown(61304)\n--/dump IsPlayerSpell(5211) and IsUsableSpell(5211) and GetSpellCooldown(5211) == 0",
